import React, { Component } from 'react';
import PokemonCard from './pokemonCard';
import axios from 'axios'; 

export default class PokemonList extends Component {
    state={
        url:'https://pokeapi.co/api/v2/pokemon?offset=0&limit=28',//change this to 151 to get all pokemon
        pokemon: null,
        candyUrl: 'https://pokemon-go1.p.rapidapi.com/pokemon_buddy_distances.json', // needs the api keys to make work 
        candyDistance: null
    }

    async componentDidMount(){
        const res = await axios.get(this.state.url);
        this.setState({pokemon: res.data['results'] });
    } // this goes in back end gehhhhhh

    //async componentDidMount(){ // im trying to get the candy api to work 
        //const resCandy = await axios.get(this.state.candyUrl); // can i have to componentDidMount? 
      //  this.setState({candyDistance : resCandy.data['results']})
    //}

    render() {
        return (
            <React.Fragment>
            {this.state.pokemon ? (
            <div className ="row" >
            {this.state.pokemon.map(pokemon =>(
                <PokemonCard
                key={pokemon}
                name = {pokemon.name}
                url ={pokemon.url}

                />
            ))}      
        </div>) : ( <h1>pokemon loading</h1>)}
        </React.Fragment>
        );
    }
}
