import React, { Component } from 'react'
import styled from "styled-components"; //may need to replace this 
import axios from 'axios';

const Sprite= styled.img`
width: 5em,
height:5em,
`;




export default class PokemonCard extends Component {
    state={
        name:'',
        imageurl:'' ,
        pokemonIndex:''
    }


    componentDidMount(){
        const{name, url} = this.props;
        const pokemonIndex =url.split("/")[url.split("/").length -2]; //what this?
        const imageurl =`https://github.com/PokeAPI/sprites/blob/master/sprites/pokemon/${pokemonIndex}.png?raw=true`

        this.setState({
            name: name, 
            imageurl: imageurl,
            pokemonIndex: pokemonIndex})
    }
    addPokemon=()=>{
        const body={
            pokemon_name:this.state.name,
            pokemon_url:this.state.imageurl,
            pokemon_id:this.state.pokemonIndex
        }
        axios.post('http://localhost:4000/walkList',body);
    }
    render() {
        const {name,url } = this.props;

        return (

            <div className ="col-md-3 col-sm-6 mb-5 ">
                <div className="card">
                        <h5 className="cardHeader">{this.state.pokemonIndex}</h5>
                            <h6 className="PokemonName">{this.state.name}</h6>
                                <Sprite className = "card-img-top rounded"
                                            src={this.state.imageurl}
                                />


                    <div className="cardBody">

                        <button onClick={this.addPokemon} > Look a button</button>  
                    </div>

                </div>
            </div>
        )
    }
}


//button needs to make an axios call that hits this endpoint. format state on component to match data base columns.
// axios call is going to send the formated state as the body. 

//walk list when it loads needs to grab the walk list from the data base and display it.
